sources.scss/snipcart_scss/snipcart.scss:
/**
    SNIPCART V2

    We strongly use BEM methodology
    Every rule must be written within .snip-layout class, to prevent basic conflict with your css
    Every class name must be prefix with "snip-"

    IMPORTANT you have to include snipcart's stylesheet AFTER all your stylesheets
*/

/*------------------------------------*\
    GLOBAL IMPORTS
\*------------------------------------*/

// Global mixins, functions
@import "./Tools/_mixins.scss";

// Global settings, mostly variables
@import "./Settings/_fonts.scss";
@import "./Settings/_mediaQueries.scss";
@import "./Settings/_colors.scss";


/*------------------------------------*\
    GLOBAL VARIABLES
\*------------------------------------*/

$padding-small: 1em;


/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_base-padding: 1.5em 0;

$_base-z-index: 1040;


/*------------------------------------*\
    STYLE
\*------------------------------------*/

// Body override
body.snip-open {
    height: auto !important;
    margin: 0 !important;
    overflow: hidden !important;
    padding: 0 !important;

    // Fix body scroll and input focus issue in iOS11 Safari
    @media #{$mq-phone} {
        position: fixed;
        width: 100%;
    }
}

// Base layout
.snip-layout {
    background: rgba(123, 123, 123, .75);
    bottom: 0;
    color: palette(layout, color);
    font-family: $font-base;
    font-size: fontsize(reset, base);
    left: 0;
    line-height: normal;
    overflow-x: auto;
    padding: 0;
    position: fixed;
    right: 0;
    top: 0;
    z-index: $_base-z-index;

    // Compound settings and reset
    @import "./Settings/_icons.scss";
    @import "./Settings/_reset.scss";

    // components
    @import "./Components/_button.scss";

    @import "./Components/_layout.scss";
    @import "./Components/_header.scss";
    @import "./Components/_footer.scss";

    @import "./Components/_action.scss";
    @import "./Components/_table.scss";
    @import "./Components/_product.scss";
    @import "./Components/_quantity-trigger.scss";
    @import "./Components/_loader.scss";
    @import "./Components/_form.scss";
    @import "./Components/_step.scss";
    @import "./Components/_cols.scss";
    @import "./Components/_error.scss";
    @import "./Components/_static.scss";
    @import "./Components/_tooltip.scss";
    @import "./Components/_flash.scss";
    @import "./Components/_discount.scss";
    @import "./Components/_badge.scss";

    // Responsive
    @media #{$mq-large} {
        font-size: fontsize(reset, large);
        padding: $_base-padding;
    }
}

sources.scss/snipcart_scss/Tools/_mixins.scss:
/**
    MIXINS

    [1]. Used to clear floating elements within a container
**/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_reset-form-transition: border .66s ease;

// [1]
@mixin clearfix {
    &::after {
        clear: both;
        content: "";
        display: table;
    }
}

@function fontsize($block, $size) {
    @if not map-has-key($fontsize, $block) {
      @warn "No palette found for `#{$block}` in $styleguide map. Property omitted.";
    }

    @return map-get(map-get($fontsize, $block), $size);
}

@function palette($palette, $tone) {
    @if not map-has-key($palettes, $palette) {
      @warn "No palette found for `#{$palette}` in $palettes map. Property omitted.";
    }

    @return map-get(map-get($palettes, $palette), $tone);
}

@mixin input-reset {
    background: palette(form, background);
    border-radius: 3px;
    border: 1px solid palette(form, border);
    box-sizing: border-box;
    color: palette(form, color);
    display: block;
    font-size: fontsize(reset, base);
    height: auto;
    line-height: 18px;
    margin: 0;
    padding: .571em .429em;
    transition: $_reset-form-transition;
    width: 100%;

    &:focus,
    &:hover {
        border-color: palette(form, border--focus);
    }
}

sources.scss/snipcart_scss/Settings/_fonts.scss:
/**
    CUSTOM FONT FAMILY

    We don't want to load many fonts on your site so we stay classic.
    You can add your custom fonts here and/or define your own fonts family variables.
*/
$font-base: Arial, sans-serif;


/**
    ICONS FONT
*/
@font-face {
    font-family: "Snipcart";
    src: url('./snipcart_scss/fonts/snipcart/Snipcart.woff?94043836') format("woff"),
         url('./snipcart_scss/fonts/snipcart/Snipcart.ttf?94043836') format("truetype");
    font-weight: normal;
    font-style: normal;
}


/**
    FONT HELPERS

    If you use custom fonts and you don't want browser side weighted/italic fonts:
    - remove font-weight (or add font-weight: normal for example)
    - add your font-family. Ex: font-family: 'myFont-bold';
*/
@mixin font-bold {
    font-weight: 700;
}


/**
    FONT SIZES
*/
$fontsize: (
    action: (
        small: 12px
    ),
    button: (
        base: 12px
    ),
    error: (
        base: 14px
    ),
    flash: (
        base: 12px
    ),
    footer: (
        base: 10px
    ),
    form: (
        small: 12px,
        base: 14px
    ),
    header: (
        small: 14px,
        large: 30px
    ),
    product: (
        base: 14px,
        large: 20px,
        icon: 30px
    ),
    quantity-trigger: (
        large: 18px,
        x-large: 22px
    ),
    reset: (
        base: 14px,
        large: 16px
    ),
    static: (
        small: 12px,
        base: 14px
    ),
    step: (
        small: 10px,
        icon: 34px
    ),
    table: (
        small: 12px,
        base: 14px,
        large: 20px
    ),
    discount: (
        icon: 20px
    )
);

sources.scss/snipcart_scss/Settings/_icons.scss:
/**
    ICON

    We use icons font. You can use them with css pseudo elements.
    You can also use your own icon set. There's many online services to generate your iconfont (fontello, glyphter, icomoon, ...).
*/

/*------------------------------------*\
    GLOBAL VARIABLES
\*------------------------------------*/

$snip-ico__font: "Snipcart";

$snip-ico--credit-cart: "\e805";
$snip-ico--truck: "\e804";
$snip-ico--letter: "\e803";
$snip-ico--receipt: "\e802";
$snip-ico--checklist: "\e801";
$snip-ico--basket: "\e800";
$snip-ico--check: "\e806";
$snip-ico--close: "\e807";


/*------------------------------------*\
    STYLE
\*------------------------------------*/

.snip-ico {
    -webkit-font-smoothing: antialiased;
    font-family: $snip-ico__font;
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    line-height: 1;
    speak: none;
    text-transform: none;
}

.snip-ico--credit-cart:before { content: $snip-ico--credit-cart; }
.snip-ico--truck:before { content: $snip-ico--truck; }
.snip-ico--letter:before { content: $snip-ico--letter; }
.snip-ico--receipt:before { content: $snip-ico--receipt; }
.snip-ico--checklist:before { content: $snip-ico--checklist; }
.snip-ico--basket:before { content: $snip-ico--basket; }
.snip-ico--check:before { content: $snip-ico--check; }
.snip-ico--close:before { content: $snip-ico--close; }

sources.scss/snipcart_scss/Settings/_reset.scss:
/**
    RESET

    We reset to default value for each elements used in snipcart.
    Your style must not impact our style.
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_reset-link-transition: background .33s ease;

/*------------------------------------*\
    STYLE
\*------------------------------------*/

div,
span,
h1,
h2,
h3
h4,
p,
a,
img,
em,
small,
strong,
ol,
ul,
li,
form,
label,
table,
tbody,
thead,
tr,
th,
td,
dl,
dt,
dd {
    border: 0;
    box-sizing: border-box;
    font-size: 100%;
    font: inherit;
    line-height: inherit;
    margin: 0;
    padding: 0;
}

a,
button {
    text-decoration: none;
    transition: $_reset-link-transition;
}

table {
    border-collapse: collapse;
    border-spacing: 0;
}

th,
td {
    vertical-align: middle;
}

ol,
ul {
    list-style: none;
}

input,
select {
    &:focus,
    &:hover {
        box-shadow: none;
        outline: none;
    }
}

input[type=text],
input[type=password],
select {
    @include input-reset
}

sources.scss/snipcart_scss/Components/_button.sc:
/**
    BUTTON

    [1]. Classic gray button
    [2]. Smaller button for tooltip
    [3]. Next or validate yellow action button
    [4]. Position classes: right, left, full size button
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_btn-size: 36px;


/*------------------------------------*\
    STYLE
\*------------------------------------*/

// [1]
.snip-btn {
    @include font-bold;
    background-color: palette(button, background);
    border-radius: 3px;
    border: 0;
    color: palette(button, color);
    cursor: pointer;
    display: inline-block;
    font-size: fontsize(button, base) / 1.2;
    height: $_btn-size;
    line-height: $_btn-size;
    outline: 0;
    padding: 0 1em;
    text-transform: uppercase;

    &:hover {
        background-color: palette(button, background--hover);
    }

    @media #{$mq-medium} {
        font-size: fontsize(button, base);
        padding: 0 2em;
    }
}

// [2]
.snip-btn--small {
    height: auto;
    line-height: normal;
    padding: .417em;
}

// [3]
.snip-btn--highlight {
    background: palette(button, background--highlight);
    color: palette(button, color--highlight);

    &:hover {
        background-color: palette(button, background--highlight-hover);
    }
}

// [4]
.snip-btn--left {
    float: left;
}

.snip-btn--right {
    float: right;
    margin-left: 1em;
}

.snip-btn--danger {
    background-color: palette(button, background--danger);

    &:hover {
        background-color: palette(button, background--danger-hover);
    }
}

.snip-btn--full {
    display: block;
    margin-top: 1em;
    text-align: center;
}

sources.scss/snipcart_scss/Components/_layout.sc:
/**
    LAYOUT

    Snipcar main container, basic content container, modal elements, loading screen
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_layout-border: 1px solid palette(layout, border);

$_layout-close-font-size: 1.5em;

$_layout-content-border: 1px solid palette(layout, content-border);


/*------------------------------------*\
    STYLE
\*------------------------------------*/

.snip-layout__main-container {
    background: palette(layout, background);
    margin: 0 auto;
    max-width: 62.25em;
    position: relative;
}

.snip-layout__close {
    color: palette(layout, close-color);
    font-size: $_layout-close-font-size * 1.2;
    padding: .333em;
    padding: 0;
    position: absolute;
    right: .417em;
    top: .1em;
    z-index: 2;

    @media #{$mq-medium} {
        font-size: $_layout-close-font-size;
        top: .208em;

        &.js-hidden {
            display: none;
        }
    }
}

.snip-layout__loading {
    @include font-bold;
    color: palette(layout, loading-color);
    padding: 5em 0;
    text-align: center;
    text-transform: uppercase;
    margin-top: -0.3em;

}

.snip-layout__content {
    padding: 1em 0;

    @media #{$mq-medium} {
        padding: 2em 0;
    }
}

.snipcart-step{
    &--inner-loading{
        .snip-layout__loading{
            display:block;
        }
        .snip-layout__content{
            display:none;
        }
    }

    &--inner-loaded{
        .snip-layout__loading{
            display:none;
        }
        .snip-layout__content{
            display:block;
        }
    }
}

.snip-layout__content--highlight {
    background-color: palette(layout, content-background);
    border-bottom: $_layout-content-border;
    border-top: $_layout-content-border;
    padding: 1em 0;
}

.snip-layout__content--bordered {
    border-bottom: $_layout-border;
}

.snip-layout__subtitle {
    padding: 0 1em;
    text-transform: uppercase;
    font-size: 18px;
}

sources.scss/snipcart_scss/Components/_header.sc:
/**
    HEADER

    Top content for the Cart

    [1]. Connected user welcome message.
    [2]. If you keep the "js-show" class, title will be displayed on small size when there's no total block.
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_header-height: 105px;
$_header-total-height: 38px;


/*------------------------------------*\
    STYLE
\*------------------------------------*/

.snip-header {
    @include font-bold;
    background: palette(header, background);
    color: palette(header, color);
    padding: $padding-small;
    position: relative;
    text-align: left;
    text-transform: uppercase;

    @media #{$mq-medium} {
        color: palette(header, color);
        height: $_header-height;
        padding: 0;
        text-align: center;
    }
}

// [1]
.snip-header__user {
    background: palette(header, background--highlight);
    font-size: fontsize(header, small);
    padding: .714em 1.429em;
    text-align: center;
}

.snip-header__user-text {
    max-width: 90%;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    margin: auto;
}

.snip-header__back {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    display: flex;
    background: palette(header, background--highlight);
    font-size: fontsize(header, small);
    padding: .714em 1.429em;
}

.snip-header__back--button {
  cursor: pointer;
  cursor: hand;
  margin: auto;
}

.snip-header__back--button:hover {
  color: palette(header, color);
}


.snip-header__user-mail {
    color: palette(header, color);
}

.snip-header__title {
    display: none;
    font-size: fontsize(header, large) / 2;
    text-align: center;

    // [2]
    &.js-show {
        display: block;
    }

    @media #{$mq-medium} {
        display: block;
        font-size: fontsize(header, large);
        left: 1.25em;
        line-height: $_header-height;
        position: absolute;
        text-align: left;
    }
}

.snip-header__total {
    background: palette(header, background--highlight);
    border-radius: 5px;
    display: inline-block;
    padding: .5em;

    @media #{$mq-medium} {
        height: $_header-total-height;
        line-height: $_header-total-height;
        margin-top: 2.188em;
        padding: 0 .625em;
    }
}

.snip-header__total-label {
    color: palette(header, label-color);
    margin-right: .625em;
}

.snip-header__continue {
    display: none;

    @media #{$mq-medium} {
        &.js-show {
            display: block;
            margin-top: -1.5em;
            position: absolute;
            right: 1.25em;
            top: 50%;
        }
    }
}

.snip-header__placeorder {
    @media #{$mq-medium} {
            display: block;
            margin-top: -1.5em;
            position: absolute;
            right: 1.25em;
            top: 50%;
    }
}

sources.scss/snipcart_scss/Components/_footer.sc:
/**
