sources.scss/snipcart_scss/Components/_table.scs:
/**
    TABLE

    [1]. Special jedi's mind trick, thanks to inheritance, we only style tbody or thead elements.
         Since we can't define `padding` or `border` for tbody, thead, tr elements, it's easier.
         And for mobile block table behavior, style is already set for those elements.
    [2]. Exception to low specificity rule, as it removes a lot of classes.
         For product table, on small device screen size, we use block display rather than table display.
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_table-border: 1px solid palette(table, border);

$_notice-warning--background-color: lighten(palette(flash, background--warning), 20%);
$_notice-warning--text-color: palette(static, color--highlight);

$_notice-error--background-color: lighten(palette(flash, background--error), 40%);
$_notice-error--text-color: palette(static, color--highlight);


/*------------------------------------*\
    STYLE
\*------------------------------------*/

.snip-table {
    font-size: fontsize(table, base);
    text-align: left;
    width: 100%;

    // [1]
    td, th, tr {
        border-bottom: $_table-border;
        padding: inherit;

        &.snip-table__row--no-border {
          border-bottom: none;
          td {
            border-bottom: 0;
          }
        }
    }
}

.snip-table {
  .snip-table__cell__notices {
    margin: 0;
    padding: 0;

    div {
      margin: 10px 20px 5px 20px;
      padding: 10px;
      border-radius: 5px;

      &.snip-table__cell__notices--warning {
        background-color: $_notice-warning--background-color;
        color: $_notice-warning--text-color;
      }

      &.snip-table__cell__notices--error {
        background-color: $_notice-error--background-color;
        color: $_notice-error--text-color;
      }
    }
  }
}

.snip-table--small {
    // [2]
    @media #{$mq-small} {
        display: block;
        font-size: fontsize(table, base);
        text-align: left;

        td, th, tr {
            display: block;

            &:empty {
                padding: 0;
                width: 0;
            }
        }

        tr {
            @include clearfix;
            padding: $padding-small 0;
            position: relative;

            + tr {
                //border-top: $_table-border;
            }
        }

        td {
            border: 0;
            padding: $padding-small;
        }

        .snip-table__header {
            display: none;
        }

        .snip-table__body {
            border: 0;
            display: block;
            padding: 0;

            &:empty {
                padding: 0;
            }

            ~ .snip-table__body {
                border-top: $_table-border;

                &:empty {
                    border: 0;
                }
            }
        }

        .snip-table__body--small,
        .snip-table__body--big {
            text-align: center;

            tr,
            td {
                padding: .5em;
                text-align: inherit;
            }
        }
    }
}

.snip-table__header {
    font-size: fontsize(table, small);
    padding: 1em 2em;
    text-transform: uppercase;

    ~ .snip-table__header {
        border-top: $_table-border;
    }
}

.snip-table__body {
    border-top: $_table-border;
    display: table-row-group;
    padding: 1.429em;
}

.snip-table__body--small {
    background: palette(table, background--highlight);

    @media #{$mq-medium} {
        padding: .429em 1.429em .429em 2.857em;
    }
}

.snip-table__body--big {
    font-size: fontsize(table, large);

    @media #{$mq-medium} {
        padding: 1.222em 1.111em 1.222em 1.222em;
    }
}

.snip-table__loading {
    text-align: center;
    font-size: fontsize(table, base);
    color: palette(table, color--highlight);
    font-weight: bold;
    text-transform: uppercase;
}

.snip-table__cell--center {
    text-align: center;
}

.snip-table__cell--long-text {
    @media #{$mq-medium} {
      max-width: 20em;
    }
}

.snip-table__cell--right {
    text-align: right;
}

.snip-table__cell--clear {
    @include clearfix;
}

.snip-table__cell--invalid {
    text-decoration: line-through;
    font-weight: normal;
    display: block;
}

.snip-table__cell--bold {
    @include font-bold;
}

.snip-table__cell--highlight {
    @include font-bold;
    color: palette(table, color--highlight);
}

.snip-table__cell--success {
    @include font-bold;
    color: palette(table, color--success);
}

.snip-table__small-col {
    @media #{$mq-small} {
        float: left;
        margin-top: $padding-small * 2;
        text-align: center;
        width: 33.333333%;

        + .snip-table__small-col {
            border-left: $_table-border;
        }
    }
}

sources.scss/snipcart_scss/Components/_product.s:
/**
    PRODUCT

    Product component, used for product items, shipping choices and payment methods

    [1]. Do not remove ".js-selected" class
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_product-border: 1px solid palette(product, border);

$_product-thumbnail-size: 65px;

$_product-customfield-size: 200px;

$_product-disabled-opacity: 0.2;


/*------------------------------------*\
    STYLE
\*------------------------------------*/

.snip-product {
    @include clearfix;
    background: palette(product, background);
    border-top: $_product-border;
    font-size: fontsize(product, base);
    padding: 2em 1.25em;
}

.snip-product--selectable {
    cursor: pointer;
}

.snip-product--selectable-item {
    background: url("./snipcart_scss/img/checkbox.png") no-repeat 0 center;
    padding-left: 2.286em;

    // [1]
    &.js-selected {
        background-image: url("./snipcart_scss/img/selected.png");
    }
}

.snip-product__thumbnail {
    display: none;
    float: left;
    height: $_product-thumbnail-size;
    margin-right: .714em;
    width: $_product-thumbnail-size;

    img {
        max-height: 100%;
        max-width: 100%;
    }

    @media #{$mq-medium} {
        display: block;
    }
}

.snip-product__name {
    @include font-bold;
    color: palette(product, color--highlight);
    font-size: 1.125em;
    overflow: hidden;
    text-transform: uppercase;

    @media #{$mq-medium} {
        font-size: fontsize(product, base);
        margin-top: .5em;
    }
}

.snip-product__description {
    font-size: fontsize(product, base) / 1.125;
    margin-top: .25em;
    overflow: hidden;

    @media #{$mq-medium} {
        font-size: fontsize(product, base);
        margin-top: .5em;
    }
}

.snip-product__important {
    @include font-bold;
    font-size: fontsize(product, base) / 1.125;

    @media #{$mq-medium} {
        font-size: fontsize(product, base);
    }
}

.snip-product__remove {
    color: palette(product, color--error);
    font-size: fontsize(product, icon);
    position: static;

    @media #{$mq-small} {
      position: absolute;
      right: $padding-small / 3.5;
      top: $padding-small / 1.75;
    }

    @media #{$mq-medium} {
        float: left;
        margin: -.154em .192em 0 0;
    }
}

.snip-product__remove--disabled {
    opacity: $_product-disabled-opacity;
    cursor: default;
}

.snip-product__customfields {
    @media #{$mq-medium} {
        max-width: $_product-customfield-size;
    }
}

.snip-product__customfields-checkbox {
    float: left;
    margin: 0 .5em .75em 0;
}

.snip-product__empty {
    font-size: fontsize(product, large);
    padding: 1em 2em;
    text-align: center;
    text-transform: uppercase;
}

sources.scss/snipcart_scss/Components/_quantity-:
/**
    QUANTITY TRIGGER

    Component to modify product quantity in the cart

    [1]. We use flexbox here since overall browser support is about 95% worldwide.
    [2]. Also, as fallback, we 'float' the columns. If your browser doesn't support flexbox, columns will be
         properly positionned even if the columns won't have the same height (graceful degradation).
    [3]. You can choose your own symbol (or even custom icon font) for add and remove product.
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

// [3]
$_qt-text-add: "+";
$_qt-text-remove: "-";


/*------------------------------------*\
    STYLE
\*------------------------------------*/

// [1]
.snip-quantity-trigger {
    @include font-bold;
    align-items: center;
    color: palette(quantity-trigger, color);
    display: flex;
    justify-content: center;
    margin: 0 auto;
}

.snip-quantity-trigger__text {
    // [2]
    float: left;
    padding: 0 .313em;
}

.snip-quantity-trigger__btn--duplicate {
    background-color: palette(quantity-trigger, background--hover);
    border-radius: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: palette(quantity-trigger, color);
    font-size: fontsize(quantity-trigger, x-large);
    font-weight: 400;
    margin-top: -1.6px;
    line-height: 1.6;
    height: 1em;
    width: 1em;

    &:hover {
      background-color: palette(button, background--highlight-hover);
    }

    &:before {
        display: block;
        height: 1em;
        width: 1em;
    }
}

.snip-quantity-trigger__btn {
    // [2]
    background-color: palette(quantity-trigger, background);
    border-radius: 30px;
    color: inherit;
    float: left;
    font-size: fontsize(quantity-trigger, large);
    font-weight: 400;
    height: 1em;
    width: 1em;

    &:hover {
        background-color: palette(quantity-trigger, background--hover);
    }

    &:before {
        display: block;
        height: 1em;
        width: 1em;
    }
}

.snip-quantity-trigger__btn--add {
    &:before {
        // [3]
        content: $_qt-text-add;
        line-height: 1em;
    }
}

.snip-quantity-trigger__btn--minus {
    &:before {
        // [3]
        content: $_qt-text-remove;
        line-height: 14px;
    }
}

.snip-quantity-trigger__btn--disabled {
    background-color: palette(quantity-trigger, background--disabled);
    color: palette(quantity-trigger, color--disabled);
    cursor: default;

    &:hover {
        background-color: palette(quantity-trigger, background--disabled);
    }
}

sources.scss/snipcart_scss/Components/_loader.sc:
/**
    LOADER

    Loader element for product modifications
    
    [1]. Do not remove ".js-active" class.
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_loader-size: 20px;
$_big-loader-height: .3em;


/*------------------------------------*\
    STYLE
\*------------------------------------*/

.snip-loader {
    // [1]
    &.js-active {
        background-image: url(./snipcart_scss/img/ajax-loader.gif);
        background-position: center center;
        background-repeat: no-repeat;
    }
}

.snip-loader--table {
    // [1]
    &.js-active {
        background-size: $_loader-size $_loader-size;
        height: $_loader-size;
        width: $_loader-size;
    }
}

.snip-loader--bar {
    height: $_big-loader-height;
    width: 100%;
    position: relative;
    overflow: hidden;
    background-color: palette(loader, background);
    margin-top: -.3em;

    &:before{
        display: block;
        position: absolute;
        content: "";
        left: -200px;
        width: 30%;
        height: $_big-loader-height;
        background-color: palette(loader, background--highlight);
        animation: snip-loading 2s ease-in-out infinite;
    }

    &.js-hidden {
        display: none;
    }
}

@keyframes snip-loading {
    from {left: -30%; }
    to {left: 100%;}
}

sources.scss/snipcart_scss/Components/_form.scss:
/**
    FORM

    Form elements, except for basic input and select styling (check _reset.scss).
    Also form fields errors.
*/

/*------------------------------------*\
    LOCAL VARIABLES
\*------------------------------------*/

$_form-border: 1px solid palette(form, border);


/*------------------------------------*\
    STYLE
\*------------------------------------*/

.snip-form--small {
    padding: 2em 15%;
    text-align: center;
}

.snip-form--discount {
    float: right;
    margin-right: .5em;
    max-width: 50%;

    @media #{$mq-medium} {
        margin-right: 1em;
        max-width: none;
    }
}

.snip-form__container {
    @include clearfix;
    clear: left;

    + .snip-form__container {
        margin-top: 1em;
    }
}

.snip-form__label {
    color: palette(form, label-color);
    display: block;
    font-size: fontsize(form, small);
    margin: 0 0 .5em 0;
    overflow: hidden;
    text-transform: uppercase;
    cursor: pointer;
}

.snip-form__description {
    margin: 1em 0;
}

.snip-form__help {
    @include font-bold;
    color: palette(form, color--highlight);
    float: right;
    font-size: fontsize(form, small);

